<script>
// 游戏状态管理
const gpGameStates = new Map();

// 初始化游戏状态
function gpInitGameStates() {
    const gameCards = document.querySelectorAll('.gp-game-card');
    gameCards.forEach((card, index) => {
        const gameId = `gp-game-${index}`;

        gpGameStates.set(gameId, {
            isFullscreen: false
        });

        // 添加按钮事件监听
        const btnFullscreen = card.querySelector('.gp-btn-fullscreen');
        const btnReload = card.querySelector('.gp-btn-reload');

        btnFullscreen.addEventListener('click', () => gpToggleFullscreen(gameId));
        btnReload.addEventListener('click', () => gpReloadGame(gameId));
    });
}

// 切换全屏模式
function gpToggleFullscreen(gameId) {
    const state = gpGameStates.get(gameId);
    if (!state) return;

    const card = document.getElementById(gameId);

    if (card.classList.contains('gp-fullscreen')) {
        // 退出全屏
        card.classList.remove('gp-fullscreen');
        document.body.style.overflow = "auto";
        state.isFullscreen = false;
    } else {
        // 进入全屏
        card.classList.add('gp-fullscreen');
        document.body.style.overflow = "hidden";
        state.isFullscreen = true;

        // 添加退出全屏的ESC键支持
        document.addEventListener('keydown', gpHandleEscape);
    }
}

// 处理ESC键退出全屏
function gpHandleEscape(event) {
    if (event.key === 'Escape') {
        const fullscreenCards = document.querySelectorAll('.gp-game-card.gp-fullscreen');
        if (fullscreenCards.length > 0) {
            const card = fullscreenCards[0];
            const gameId = card.id;
            const state = gpGameStates.get(gameId);

            if (state) {
                card.classList.remove('gp-fullscreen');
                document.body.style.overflow = "auto";
                state.isFullscreen = false;
                document.removeEventListener('keydown', gpHandleEscape);
            }
        }
    }
}

// 重新加载游戏
function gpReloadGame(gameId) {
    const frame = document.querySelector(`#${gameId} iframe.gp-game-frame`);
    if (!frame) return;

    const src = frame.src;
    frame.src = '';
    setTimeout(() => {
        frame.src = src;
    }, 100);
}

// 初始化页面
document.addEventListener('DOMContentLoaded', gpInitGameStates);
</script>
